mapper.select:
select count(1)
from t_collect
where is_delete = 0
and created_by = #{userId}
and moment_id = #{id}
and status = 1

mapper.update:
update t_collect
set is_delete = 1
where moment_id = #{momentId}

update t_collect
set is_delete = 1
where created_by = #{createdBy}
and moment_id = #{momentId}

mapper.select:
select count(1)
from t_collect
where is_delete = 0
and created_by = #{createdBy}
and moment_id = #{momentId}
and status = 1
